function isValid(s) {
    let flag = true;
    let count = 0;
    let cur;
    let flag2 = true;
    let SET = Array.from(new Set(Array.from(s)));

    for (let i = 1; i < SET.length; i++) {
        let x = [...s.matchAll(SET[i])].length;
        let y = [...s.matchAll(SET[i - 1])].length;
        console.log(x, y, SET[i], SET)
        if (x == y) {
            if (flag2) {
                cur = x;
                flag2 = false;
            }
        }
        else if ( flag) {
            count++;
            if (count == 2) flag = false
        }
        else return "NO"
        if (x != cur && y != cur) return "NO"
    }

    return "YES"
}
function main() {
    let s = ""
    console.log(isValid(s));
}
